                values: new object[] { " \r\n\r\n<div class=\"container\">\r\n    <div class=\"refund-container\">\r\n        <h3><i class=\"fas fa-undo-alt\"></i> Refund & Cancellation Policy</h3>\r\n        <p class=\"refund-intro\">At MovieStar Cinema, we strive to provide the best experience. However, we understand that plans may change.</p>\r\n\r\n        <div class=\"accordion\" id=\"refundAccordion\">\r\n            <!-- Ticket Refunds -->\r\n            <h4 class=\"mt-3\"><i class=\"fas fa-ticket-alt\"></i> Ticket Refunds</h4>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingOne\">\r\n                    <button class=\"accordion-button\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseOne\">\r\n                        Q: Can I get a refund for my ticket?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseOne\" class=\"accordion-collapse collapse show\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: Yes, tickets are refundable if canceled at least **24 hours before the showtime**.  \r\n                        Refunds are processed within **3-5 business days**.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Cancellations Due to Technical Issues -->\r\n            <h4 class=\"mt-3\"><i class=\"fas fa-exclamation-circle\"></i> Cancellations Due to Technical Issues</h4>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingTwo\">\r\n                    <button class=\"accordion-button collapsed\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseTwo\">\r\n                        Q: What happens if the movie is canceled?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseTwo\" class=\"accordion-collapse collapse\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: If a movie is **canceled due to technical issues**, we offer a **full refund**  \r\n                        or the option to **rebook for another date at no additional cost**.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- No-Show Policy -->\r\n            <h4 class=\"mt-3\"><i class=\"fas fa-ban\"></i> No-Show Policy</h4>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingThree\">\r\n                    <button class=\"accordion-button collapsed\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseThree\">\r\n                        Q: What if I miss my show?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseThree\" class=\"accordion-collapse collapse\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: If you **fail to attend your movie** without canceling at least 24 hours in advance,  \r\n                        **no refund will be issued**. We recommend managing your bookings carefully.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Payment and Refund Requests -->\r\n            <h4 class=\"mt-3\"><i class=\"fas fa-credit-card\"></i> Payment & Refund Requests</h4>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingFour\">\r\n                    <button class=\"accordion-button collapsed\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseFour\">\r\n                        Q: How do I request a refund?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseFour\" class=\"accordion-collapse collapse\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: To request a refund, please contact our support team at **info@gmail.com**  \r\n                        with your **booking details and reason for refund**.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingFive\">\r\n                    <button class=\"accordion-button collapsed\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseFive\">\r\n                        Q: What payment methods are accepted?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseFive\" class=\"accordion-collapse collapse\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: We accept payments via **credit/debit cards, PayPal, and Stripe**.  \r\n                        Refunds are processed back to the **original payment method**.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <div class=\"accordion-item\">\r\n                <h2 class=\"accordion-header\" id=\"headingSix\">\r\n                    <button class=\"accordion-button collapsed\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#collapseSix\">\r\n                        Q: Are gift cards refundable?\r\n                    </button>\r\n                </h2>\r\n                <div id=\"collapseSix\" class=\"accordion-collapse collapse\" data-bs-parent=\"#refundAccordion\">\r\n                    <div class=\"accordion-body\">\r\n                        A: Unfortunately, **gift cards are non-refundable** and cannot be exchanged for cash.\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n        </div>\r\n\r\n        <p class=\"text-center mt-4\">For further assistance, contact us via <a href=\"#\">Customer Support</a>!</p>\r\n    </div>\r\n</div>\r\n \r\n", "http://localhost/Images/photos/refund.png" });

            migrationBuilder.UpdateData(
